package com.tantan.jvm.designpattern.adapter.interfaceadapter;

//目标2类：只需要调用method2
public class SourceSub2 extends Wrapper2 {

	//自己只需method2:只为调用method2 不需要调用method1 ,所以不需要重写method1
	@Override
	public void method2() {
		
		//覆盖父类的方法
		System.out.println("SourceSub2:the sourceable interface's second Sub2!");  
	}
}
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     